class endereço:
    def __init__(self, rua, cidade):
     self.rua = rua 
     self.cidade = cidade

    def mostrar_endereco(self):
        return f"{self.rua}, {self.cidade}"
    
class pessoa:
    def __init__(self, nome, endereco):
        self.nome = nome
        self.endereco = endereco # a associação acontece aqui!

    def mostrar_informaçoes(self):
        return f"{self.nome} mora em {self.endereco.mostrar_endereco()}"
    
endereço_maria = endereço("av.principal", "são paulo")

maria = pessoa("maria", endereço_maria)

print(maria.mostrar_informaçoes())



          